import React, { useEffect, useState } from 'react'


const Poll = ({ price, value }) => {
    const [data, setData] = useState(price)
    const negValues = [-1, 1]

    useEffect(() => {
        const interval = setInterval(() => {
            let negRandom = Math.floor(Math.random() * 2)
            let random = Math.floor(Math.random() * 10) * negValues[negRandom]
            console.log(Number(price) + random)
            setData(Number(price) + random)

        }, 2000);

        // Clearing the interval 
        return () => clearInterval(interval);
    }, [price]);



    return (
        <>
            <h1>{data}</h1>
        </>
    )
}

export default Poll
