import './App.css';
import { useEffect, useState } from 'react'
import axios from "axios"



function App() {

  const [value, setValue] = useState('Choose');
  const [stocks, setStocks] = useState([]);
  const [selected, setSelected] = useState(false)
  const [price, setPrice] = useState(0)


  const fetchData = async () => {

    const { data } = await axios.get('/stock/name')
    setStocks(data)
    console.log(stocks)
    // return price
  }



  useEffect(() => {
    fetchData()
  }, [])



  const findPrice = () => {
    stocks.map(stock => {
      if (value === stock.name) {
        setPrice(stock.currPrice)
      }
    })

    console.log(price)
  }


  const handleChange = (e) => {
    setValue(e.target.value);
    setSelected(true)
  };


  return (
    <>
      <h1
        className='title'
      >Welcome to Stock Price Tracker</h1>

      <div className='select-options'>
        <select className='values' value={value} onChange={handleChange}>
          <option>Choose</option>
          <option value="Reliance">Reliance</option>
          <option value="TCS">TCS</option>
          <option value="HDFC Bank">HDFC Bank</option>
          <option value="Infosys">Infosys</option>
          <option value="Zomato">Zomato</option>
          <option value="ONGC">ONGC</option>
          <option value="Nestle">Nestle</option>
          <option value="Coal India">Coal India</option>
          <option value="Siemens">Siemens</option>
          <option value="Cipla">Cipla</option>
        </select>
      </div>


      {value != 'Choose' ?
        <p className='p'>You have selected {value}</p>
        : <p className='p'>Please make a selection</p>
      }



      {stocks.map(stock => {
        if (value === stock.name) {
          setPrice(stock.currPrice)
          {console.log(price)}

        }
      }
      )}

      {/* {findPrice()} */}
    </>
  );
}

export default App;
