import React, { useEffect } from 'react'


const Poll = ({ price }) => {

    useEffect(() => {
        const interval = setInterval(() => {
            // console.log(price)
            <Poll price={price} />
        }, 1000);

        // Clearing the interval 
        return () => clearInterval(interval);
    }, [price]);


    return (
        <>
            <h1 style={{ backgroundColor: 'red' }}>{price}</h1>
        </>
    )
}

export default Poll
