import React, { useEffect, useState } from 'react'


const Poll = ({ price }) => {
    const [data, setData] = useState(price)
    let prevPrice = price

    useEffect(() => {
        const interval = setInterval(() => {
            let random = Number.parseFloat((Math.floor(Math.random() * (97.6 - (-97.1) + 1))) + -97.1)
            console.log(Number(price) + random + "," + prevPrice)
            
            setData(Number(price) + random)
            if (parseFloat(data) > parseFloat(prevPrice)) console.log('true')
            prevPrice = (Number(price) + random)
            // console.log()
        }, 4000);

        // Clearing the interval 
        return () => clearInterval(interval);
    }, [price]);





    return (
        <>
            <h1
                style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    height: '200px',
                    width: '200px',
                    marginLeft: '43%',
                    backgroundColor: 'gray',
                    marginTop: '40px',
                    color: 'white'
                }}

            >{data}</h1>
        </>
    )
}

export default Poll
