import React, { useEffect, useState } from 'react'


const Poll = ({ price }) => {
    const [data, setData] = useState(price)
    const [prev, setPrev] = useState(price)
    const [active, setActive] = useState(false)
    let prevPrice = price

    useEffect(() => {
        const interval = setInterval(() => {
            let random = Number.parseFloat((Math.floor(Math.random() * (97.6 - (-97.1) + 1))) + -97.1)
            console.log(Number(price) + random + "," + prevPrice)
            // if (parseFloat(Number(price) + random) > parseFloat(prevPrice)) console.log('true')
            setPrev(prevPrice)
            setData(Number(price) + random)
            prevPrice = (Number(price) + random)
        }, 4000);

        // Clearing the interval 
        return () => clearInterval(interval);
    }, [price]);


    const setColor = () => {
        setActive(!active)
    }



    return (
        <>
            <h1
                style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    height: '200px',
                    width: '300px',
                    marginLeft: '43%',
                    backgroundColor: 'gray',
                    marginTop: '40px',
                    color: 'white'
                }}


            >{data + "," + prev}
                {parseFloat(data) > parseFloat(prev) ? setActive(true) : setActive(false)}
            </h1>
        </>
    )
}

export default Poll
