import { createSlice } from "@reduxjs/toolkit"

const initialState = {
    price: null,
    name: null,
    data: []
}

export const priceSlice = createSlice({
    name: "dataPrice",
    initialState,
    reducers: {
        setPrice: (state, action) => {
            const updatedPrice = state.data.map((stock) => {
                if (stock.name === action.payload.stock.name) return action.payload.stock;
                return stock;
            });
            state.data = updatedPrice
        }
    }
})

export const { setPrice } = priceSlice.actions;
export default priceSlice.reducer;
